import React, {Component} from "react";

// const PlayButton = props => (
//     <div className="playButton">
//         <button
//             onClick={props.onClick}
//         >play</button>
//     </div>
// )

class PlayButton extends Component {
    constructor(props) {
        super(props);
        this.play = this.play.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        // this.submitComment = this.submitComment.bind(this);
        this.state = {
            logged_in: props.logged_in,
            username: props.username,
            userId: props.userId,
            startSess: false,
            memeSrc: "",
            commentText: "",
            commentSub: false,
            //comementNum: the number of comments that a user has submitted in a game session
        }
    }

    play = event => {
        this.setState({startSess: true})
        this.setState({memeSrc: "https://imageresizer.static9.net.au/qifWHWByMwnsA068tqYbnw_nFz8=/396x0/http%3A%2F%2Fprod.static9.net.au%2F_%2Fmedia%2F2017%2F05%2F24%2F13%2F27%2FChloe-meme-imgflip.jpg"})
        console.log("Lets play")
        console.log(this.state)

    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value);
        console.log(this.state.value);
        event.preventDefault();
    }


    // submitComment = event => {
    //     event.preventDefault();
    //     var inputA = event.target.value;
    //     var inputB = event.target.children;
    //     this.setState({commentSub: true});
    //     var comment = input[0].value;
    //     console.log("var comment: " + comment);
    //     console.log("var input: " + inputA)
    //     this.setState({commentText: comment})
    // }

    render() {
        return (
            <div className="playButton">
                {this.state.startSess === false && (
                    <button
                        onClick={this.play}
                    >
                    play
                    </button>
                )}
                {this.state.startSess === true && (
                    <div className="gameSess">
                        <img alt="sample" src={this.state.memeSrc} ></img>
                        <form onSubmit={this.handleSubmit}>
                            <input type="text" value={this.state.value} onChange={this.handleChange} name="comment" placeholder="enter caption/comment for the meme"/>
                            <button>Submit</button>
                        </form>

                    </div>
                )}
            </div>  
        )
    }
}

export default PlayButton;
