import React, {Component} from "react";

class PlayButton extends Component {
    constructor(props) {
        super(props);
        this.play = this.play.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.state = {
            logged_in: props.logged_in,
            username: props.username,
            userId: props.userId,
            startSess: false,
            memeSrc: "",
            commentText: "",
            commentSub: false,
            value:"",
            comementNum: undefined
        }
    }

    play = event => {
        this.setState({startSess: true})
        this.setState({memeSrc: "https://imageresizer.static9.net.au/qifWHWByMwnsA068tqYbnw_nFz8=/396x0/http%3A%2F%2Fprod.static9.net.au%2F_%2Fmedia%2F2017%2F05%2F24%2F13%2F27%2FChloe-meme-imgflip.jpg"})
        console.log("Lets play")
        console.log(this.state)

    }

    handleChange(event) {
        this.setState({value: event.target.value});
        var value = this.state.value;
        var valueL = value.length + 1;
        this.setState({comementNum: valueL})
    }

    handleSubmit(event) {
        // alert('A name was submitted: ' + this.state.value);
        console.log(this.state.value);
        console.log(this.state.comementNum)
        event.preventDefault();
    }

    render() {
        return (
            <div className="playButton">
                {this.state.startSess === false && (
                    <button
                        onClick={this.play}
                    >
                    play
                    </button>
                )}
                {this.state.startSess === true && (
                    <div className="gameSess">
                        <img alt="sample" src={this.state.memeSrc} ></img>
                        <form onSubmit={this.handleSubmit}>
                            <input type="text" value={this.state.value} onChange={this.handleChange} />
                            <input type="submit"  value="Submit" />
                        </form>
                        {this.state.commentNum > 40 && 
                            <h1>"Too long"</h1>
                        }
                    </div>
                )}
            </div>  
        )
    }
}

export default PlayButton;
